// @flow

import {observer} from "mobx-react";
import React from "react";
import CharacterSplash from "app/components/CharacterSplash/CharacterSplash";
import "./CharacterCard.scss";

@observer
export default class CharacterCard extends React.PureComponent {
    props: {
        characterClass: Function,
        callback?: Function
    };

    render() {
        return (
            <div className="CharacterCard" onClick={this.props.callback}>
                <div className="CharacterCard-title">{this.props.characterClass.title}</div>
                <CharacterSplash characterClass={this.props.characterClass}/>
            </div>
        )
    }
}
