// @flow

import {observer} from "mobx-react";
import React from "react";
import Character from "app/models/Character";
import "./CharacterPortrait.scss";

@observer
export default class CharacterPortrait extends React.PureComponent {
    props: {
        character: Character
    };

    render() {
        return (
            <img className="CharacterPortrait"
                 src={this.props.character.constructor.portrait}
                 alt={this.props.character.constructor.title + " portrait"}/>
        )
    }
}
