// @flow

import {observer} from "mobx-react";
import React from "react";

@observer
export default class CharacterSplash extends React.PureComponent {
    props: {
        characterClass: Function
    };

    render() {
        return (
            <img className="CharacterSplash" src={this.props.characterClass.splash}
                 alt={this.props.characterClass.name + " splash"}/>
        )
    }
}
