// @flow

import {observer} from "mobx-react";
import React from "react";
import type {DifficultyLevel} from "app/constants/difficultyLevels";
import "./DifficultyLevelBar.scss";

@observer
export default class DifficultyLevelBar extends React.PureComponent {
    props: {
        difficultyLevel: DifficultyLevel,
        linesCleared: number
    };

    render() {
        const progress = this.props.difficultyLevel.progress(this.props.linesCleared);

        return (
            <div className={`DifficultyLevelBar DifficultyLevelBar--${this.props.difficultyLevel.type}`}>
                <div className="DifficultyLevelBar-container">
                    <div className="DifficultyLevelBar-background"/>
                    <div className="DifficultyLevelBar-fill" style={{width: `${progress}%`}}/>
                </div>
            </div>
        )
    }
}
