// @flow

import {observer} from "mobx-react";
import React from "react";
import type {Difficulty} from "app/constants/difficultyLevels";
import {DIFFICULTY_LEVELS} from "app/constants/difficultyLevels";
import DifficultyLevel from "app/components/DifficultyLevelBar/DifficultyLevelBar";
import "./DifficultyLevelBars.scss";

@observer
export default class DifficultyLevelBars extends React.PureComponent {
    props: {
        linesCleared: number,
        difficulty: number
    };

    render() {
        return (
            <div className="DifficultyBars">
                {
                    DIFFICULTY_LEVELS.map((difficulty: Difficulty) => {
                        return <DifficultyLevel key={difficulty.level} difficultyLevel={difficulty}
                                                linesCleared={this.props.linesCleared}/>
                    })
                }
            </div>
        )
    }
}
