// @flow

import {observer} from "mobx-react";
import React from "react";
import {DIFFICULTY_LEVELS} from "app/constants/difficultyLevels";
import DifficultyLevelBars from "app/components/DifficultyLevelBars/DifficultyLevelBars";
import "./DifficultyMenu.scss";

@observer
export default class DifficultyMenu extends React.PureComponent {
    props: {
        linesCleared: number,
        difficulty: number
    };

    render() {
        const difficultyLevel = DIFFICULTY_LEVELS[this.props.difficulty];

        return (
            <div className={`DifficultyMenu DifficultyMenu--${difficultyLevel.type}`}>
                <header>
                    <h2 className="DifficultyMenu-level">
                        ({difficultyLevel.type}) Speed {difficultyLevel.level}
                    </h2>
                </header>

                <DifficultyLevelBars linesCleared={this.props.linesCleared} difficulty={this.props.difficulty}/>
            </div>
        )
    }
}
