// @flow

import {observer} from "mobx-react";
import React from "react";
import Effect from "app/models/Effect";
import "./Effects.scss";

@observer
export default class Effects extends React.PureComponent {
    props: {
        effects: Effect[],
        blockSize: number
    };

    render() {
        const blockSize = this.props.blockSize;

        const effects = this.props.effects.map((effect) => {
            const left = effect.position.x * blockSize + .5 + 'px';
            const top = effect.position.y * blockSize + .5 + 'px';

            return (
                <div className="Effects-effect" key={effect.id} style={{top, left}}>
                    {effect.render(blockSize)}
                </div>
            )
        });

        return (
            <div className="effects">
                {effects}
            </div>
        );
    }
}
