import {observer} from "mobx-react";
import React from "react";

import BlockModel from "app/game/Block";

import "./Block.scss";

@observer
export default class Block extends React.PureComponent {
    props: {
        x: number,
        y: number,
        block: BlockModel,
        blockSize: number,
        type: string
    };

    render() {
        const size = this.props.blockSize;
        const x = this.props.x * size;
        const y = this.props.y * size;

        const classes = ['block', this.props.type ? `block-${this.props.type}` : ''];

        return (
            <div className={classes.join(' ')}
                 style={{left: `${x + .5}px`, top: `${y + .5}px`, width: `${size - 1}px`, height: `${size - 1}px`}}
            >
            </div>
        );
    }
}
