import {observer} from "mobx-react";
import React from "react";

import Character from "app/game/Character";

import "./CharacterDetails.scss";

@observer
export default class CharacterDetails extends React.PureComponent {
    props: {
        character: Character,
        level: number
    };

    render() {
        const character: Character = this.props.character;

        return (
            <div className="character-details">
                <header>
                    <div className="character-name">{character.name}</div>
                    <div className="character-level">Level: {this.props.level}</div>
                </header>

                <div className="character-description">{character.description}</div>
            </div>
        )
    }
}
