import React from "react";
import PropTypes from "prop-types";

import Character from "app/game/Character";

import {Image} from "react-bootstrap";

import "./CharacterPortrait.scss";

const CharacterPortrait = (props) => {
    const character: Character = props.character;

    return (
        <Image className="character-portrait" src={character.portrait}/>
    )
};

CharacterPortrait.propTypes = {
    character: PropTypes.instanceOf(Character).isRequired,
    className: PropTypes.string
};

export default CharacterPortrait;
