import {observer} from "mobx-react";
import React from "react";

import Character from "app/game/Character";

import {Image} from "react-bootstrap";

import "./CharacterPortrait.scss";

@observer
export default class CharacterPortrait extends React.PureComponent {
    props: {
        character: Character
    };

    render() {
        return (
            <Image className="character-portrait" src={this.props.character.portrait}/>
        )
    }
}
