import React from "react";
import PropTypes from "prop-types";

import Character from "app/game/Character";
import CharacterPortrait from "app/components/Game/CharacterPortrait/CharacterPortrait";
import CharacterDetails from "app/components/Game/CharacterDetails/CharacterDetails";

import "./GameMenu.scss";

const Menu = ({character}) => {
    return (
        <div className="game-menu">
            <header>
                <CharacterPortrait character={character} style={{portrait: {background: 'red'}}}/>
                <CharacterDetails character={character}/>
            </header>
        </div>
    )
};

Menu.propTypes = {
    character: PropTypes.instanceOf(Character).isRequired
};

export default Menu;
