// @flow

import React from "react";
import logo from "app/assets/images/logo.png";
import "./Logo.scss";

export default class Logo extends React.PureComponent {
    props: {
        image: boolean,
        text: boolean,
    };

    static defaultProps = {
        image: true,
        text: true
    };

    render() {
        return (
            <div className="Logo">
                {this.props.image ? this.constructor.renderImage() : null}
                {this.props.text ? this.constructor.renderText() : null}
            </div>
        )
    }

    static renderImage() {
        return (
            <img src={logo} alt="Blockers logo"/>
        );
    }

    static renderText() {
        return (
            <span>Blockers</span>
        );
    }
}