// @flow

import React, {Component} from "react";
import {observer} from "mobx-react";
import "./SceneDirector.scss";

@observer
export default class SceneDirector extends Component {
    props: {
        scene: any,
        modalScene?: ?any
    };

    render() {
        return (
            <div className="SceneDirector">
                <div className="SceneDirector-scene">
                    {this.props.scene}
                </div>

                {this.renderModalScene()}
            </div>
        )
    }

    renderModalScene() {
        if (!this.props.modalScene) return null;

        return (
            <div className="SceneDirector-modalScene">
                {this.props.modalScene}
            </div>
        );
    }
}
