// @flow

import {observer} from "mobx-react";
import React from "react";
import "./ScoreMenu.scss";

@observer
export default class ScoreMenu extends React.PureComponent {
    props: {
        experience: number,
        linesCleared: number
    };

    render() {
        return (
            <div className="ScoreMenu">
                <header>
                    <h2 className="ScoreMenu-experience">{this.props.experience} pts</h2>
                </header>

                <div className="ScoreMenu-linesCleared">{this.props.linesCleared} Lines Cleared</div>
            </div>
        )
    }
}
