// @flow

import React from "react";
import SpellModel from "app/models/Spell";
import "./Spell.scss";

export default class Spell extends React.PureComponent {
    props: {
        spell: SpellModel
    };

    render() {
        return (
            <div className="Spell">
                <img className="Spell-icon" src={this.props.spell.constructor.icon}
                     alt={`${this.props.spell.constructor.title} spell icon`}/>

                <div className="Spell-popover">
                    <span className="Spell-title">{this.props.spell.constructor.title}</span>
                </div>
            </div>
        )
    }
};
