// @flow

import {observer} from "mobx-react";
import React from "react";
import FontAwesome from "react-fontawesome";

import SpellModel from "app/models/Spell";

import Spell from "app/components/Spell/Spell";

import "./SpellsMenu.scss";

@observer
export default class SpellsMenu extends React.PureComponent {
    props: {
        spells: Spell[]
    };

    render() {
        return (
            <div className="SpellsMenu">
                <header>
                    <h2>
                        <FontAwesome name="magic"/> Spells
                    </h2>
                </header>

                <div className="SpellsMenu-spells">
                    {this.props.spells.map((spell: SpellModel) => {
                        return <Spell key={spell.constructor.title} spell={spell}/>;
                    })}
                </div>
            </div>
        )
    }
}
