// @flow

import {observer} from "mobx-react";
import React from "react";
import Stage from "app/models/Stage";
import "./StageMenu.scss";

@observer
export default class StageMenu extends React.PureComponent {
    props: {
        stage: Stage,
        mode: string
    };

    render() {
        return (
            <div className="StageMenu">
                <header>
                    <h2 className="StageMenu-mode">{this.props.mode}</h2>
                </header>

                <div className="StageMenu-stage">{this.props.stage.constructor.title}</div>
            </div>
        )
    }
}
