// @flow

export type ControlActions = {
    move: Function,
    drop: Function,
    rotate: Function,
    cast: Function,
    pause: Function,
    resume: Function
};

export default class Control {
    actions: ControlActions;

    constructor(actions: ControlActions) {
        this.actions = actions;
        this.setup();
    }

    setup(): void {
        throw new Error('Setup method not defined for control ' + this.constructor.name);
    }
}
