// @flow

export default class Control {
    move: Function;
    drop: Function;
    rotate: Function;
    cast: Function;
    pause: Function;
    resume: Function;

    constructor(callbacks: any) {
        this.move = callbacks.move;
        this.drop = callbacks.drop;
        this.rotate = callbacks.rotate;
        this.cast = callbacks.cast;
        this.pause = callbacks.pause;
        this.resume = callbacks.resume;

        this.setup();
    }

    setup(): void {
        throw new Error('Setup method not defined for control ' + this.constructor.name);
    }
}