// @flow

import Mousetrap from "mousetrap";
import Control from "./Control";
import * as keyboardKeys from "app/constants/keyboardKeys";

export default class KeyboardControl extends Control {
    setup() {
        Mousetrap.bind(keyboardKeys.DECAY, () => this.actions.move(0, 1));
        Mousetrap.bind(keyboardKeys.DROP, () => this.actions.drop());
        Mousetrap.bind(keyboardKeys.ROTATE, () => this.actions.rotate());
        Mousetrap.bind(keyboardKeys.LEFT, () => this.actions.move(-1, 0));
        Mousetrap.bind(keyboardKeys.RIGHT, () => this.actions.move(1, 0));

        Mousetrap.bind(keyboardKeys.SPELL_0, () => this.actions.cast(0));
        Mousetrap.bind(keyboardKeys.SPELL_1, () => this.actions.cast(1));
        Mousetrap.bind(keyboardKeys.SPELL_2, () => this.actions.cast(2));
    }
}
