import Mousetrap from "mousetrap";

import Control from "./Control";

import * as keyboardControlKeys from "app/constants/keyboardControlKeys";

export default class KeyboardControl extends Control {
    setup() {
        Mousetrap.bind(keyboardControlKeys.DECAY, () => this.move(0, 1));
        Mousetrap.bind(keyboardControlKeys.DROP, () => this.drop());
        Mousetrap.bind(keyboardControlKeys.ROTATE, () => this.rotate());
        Mousetrap.bind(keyboardControlKeys.LEFT, () => this.move(-1, 0));
        Mousetrap.bind(keyboardControlKeys.RIGHT, () => this.move(1, 0));

        // Mousetrap.bind(keyboardControlKeys.SPELL_0, () => this.cast(0));
        // Mousetrap.bind(keyboardControlKeys.SPELL_1, () => this.cast(1));
        // Mousetrap.bind(keyboardControlKeys.SPELL_2, () => this.cast(2));
    }
}
