// @flow

import Piece from "app/game/Piece";

export default class Block {
    piece: Piece | null;
    color: any;
    id: number;

    static _id = 1;

    constructor(piece: any, color: any) {
        this.piece = piece;
        this.color = color;
        this.id = this.constructor._id++;
    }

    detach() {
        this.piece = null;
    }
}
