// @flow

import Spell from "app/game/Spell";

export default class Character {
    level: number = 1;

    name: string;
    description: string;

    spellClasses: Function[] = [];
    spells: Spell[] = [];

    portrait: string;
    splash: string;

    constructor() {
        this.spells = this.spellClasses.map((spell) => new spell());
    }
}
