// @flow

export default class Spell {
    name: string;
    passiveDescription: string;
    castDescription: string;

    widget: boolean = false;

    castable: boolean = false;
    cooldown: number = 0; // in seconds
    healthCost: number = 0;
    manaCost: number = 0;

    lastCast: number;

    cast() {
        throw new Error('Undefined cast method');
    }

    canCast(): boolean {
        return this.castable && (this.lastCast + this.cooldown > (new Date()).getTime());
    }

    renderWidget() {
        throw new Error('Undefined widget method');
    }
}
