// @flow

export default class Spell {
    name: string;
    castable: boolean;
    cooldown: number;
    lastCast: number;

    passive() {
    }

    cast() {
        throw new Error('Undefined onCast method');
    }

    canCast(): boolean {
        return this.castable && (this.lastCast + this.cooldown > (new Date()).getTime());
    }
}
