import {observer} from "mobx-react";
import React from "react";

import BlockModel from "app/game/Block";

import "./Block.scss";

@observer
export default class Block extends React.PureComponent {
    props: {
        block: BlockModel,
        blockSize: number,
        style?: Map
    };

    render() {
        const width = this.props.blockSize;
        const height = this.props.blockSize;

        const style = {
            backgroundColor: this.props.block.color,
        };

        const character = this.props.block.character;
        const className = [
            'Block',
            character ? 'Block--' + character.constructor.slug : ''
        ];

        return (
            <div className={className.join(' ')} style={{width, height, ...this.props.style || {}}}>
                <div className="Block-fill" style={style}/>
            </div>
        );
    }
}
