import {observer} from "mobx-react";
import React from "react";

import Character from "app/game/Character";

import CharacterSplash from "app/game/components/CharacterSplash/CharacterSplash";

import "./CharacterCard.scss";

@observer
export default class CharacterCard extends React.PureComponent {
    props: {
        character: Character,
        callback?: Function
    };

    render() {
        const character = this.props.character;

        return (
            <div className="CharacterCard" onClick={this.props.callback}>
                <div className="CharacterCard-title">{character.title}</div>
                <CharacterSplash character={character}/>
            </div>
        )
    }
}
