import {observer} from "mobx-react";
import React from "react";

import Character from "app/game/Character";
import CharacterPortrait from "app/game/components/CharacterPortrait/CharacterPortrait";
import CharacterBar from "app/game/components/CharacterBar/CharacterBar";

import Arc from "app/components/Arc/Arc";

import "./CharacterMenu.scss";

@observer
export default class CharacterMenu extends React.PureComponent {
    props: {
        character: Character
    };

    render() {
        const character = this.props.character;

        const xpPercentage = 30;

        return (
            <div className="CharacterMenu">
                <header className="clearfix">
                    <div className="CharacterMenu-portrait-group">
                        <div className="CharacterMenu-xp-background"/>
                        <CharacterPortrait character={character.constructor}/>
                        <Arc percentage={xpPercentage} radius={60} stroke='#6C6'/>
                    </div>

                    <div className="CharacterMenu-title">{character.constructor.title}</div>
                    <div className="CharacterMenu-level">Level {3}</div>
                </header>

                <CharacterBar icon="heart" value={character.health} max={character.maxHealth} color="#F33"/>
                <CharacterBar icon="flask" value={character.mana} max={character.maxMana} color="#33F"/>
            </div>
        )
    }
}
