import {observer} from "mobx-react";
import React from "react";

import Character from "app/game/Character";

import "./CharacterPortrait.scss";

@observer
export default class CharacterPortrait extends React.PureComponent {
    props: {
        character: Character
    };

    render() {
        return (
            <img className="CharacterPortrait" src={this.props.character.portrait} alt={this.props.character.title + " portrait"}/>
        )
    }
}
