import {observer} from "mobx-react";
import React from "react";

@observer
export default class CharacterSplash extends React.PureComponent {
    props: {
        character: Function
    };

    render() {
        return (
            <img className="CharacterSplash" src={this.props.character.splash}
                 alt={this.props.character.name + " splash"}/>
        )
    }
}
