import {observer} from "mobx-react";
import React from "react";

import "./LevelMenu.scss";

@observer
export default class LevelMenu extends React.PureComponent {
    props: {
        level: string,
        mode: string
    };

    render() {
        return (
            <div className="LevelMenu">
                <header>
                    <h2 className="LevelMenu-number">{this.props.level}</h2>
                </header>

                <div className="LevelMenu-mode">Mode: {this.props.mode}</div>
            </div>
        )
    }
}
