import React from "react";

import "./PlaygroundGrid.scss";

export default class PlaygroundGrid extends React.PureComponent {
    props: {
        rows: number,
        cols: number
    };

    static lines(amount, name, marginDirection) {
        let result = [];
        for (let i = 1; i < amount; i++) {
            const percentage = i * 100 / amount;
            result.push(<div key={i} className={`PlaygroundGrid-line-${name}`}
                             style={{[marginDirection]: percentage + '%'}}/>);
        }

        return result;
    }

    render() {
        let verticalLines = this.constructor.lines(this.props.cols, 'vertical', 'left');
        let horizontalLines = this.constructor.lines(this.props.rows, 'horizontal', 'top');

        return (
            <div className="PlaygroundGrid">
                {verticalLines}
                {horizontalLines}
            </div>
        );
    }
}
