import {observer} from "mobx-react";
import React from "react";

import "./ScoreMenu.scss";

@observer
export default class ScoreMenu extends React.PureComponent {
    props: {
        score: number,
        speed: number
    };

    render() {
        return (
            <div className="ScoreMenu">
                <header>
                    <h2 className="ScoreMenu-number">Score: {this.props.score}</h2>
                </header>

                <div className="ScoreMenu-speed">Speed: {this.props.speed}</div>
            </div>
        )
    }
}
