// @flow

import {action, observable} from "mobx";
import React from "react";

import Effect from "app/models/Effect";
import Piece from "app/models/Piece";

import Matrix from "app/components/Matrix/Matrix";

import "./SparePieceEffect.scss";

export default class SparePieceEffect extends Effect {
    @observable piece: ?Piece;

    @action setPiece(piece: Piece) {
        this.piece = piece;
    }

    render(blockSize: number) {
        const size = `${blockSize * 4 * .5}px`;
        const style = {width: size, height: size};

        return (
            <div className="SparePieceEffect" style={style}>
                <header className="SparePieceEffect-header">
                    Spare
                </header>

                {!this.piece ? null :
                    <Matrix matrix={this.piece} blockSize={blockSize * .35}/>
                }
            </div>
        )
    }
}
