// @flow

import GameEvent from "app/game/GameEvent";
import type {Position} from "app/game/Matrix";

export default class MoveEvent extends GameEvent {
    position: Position;
    newPosition: Position;
    decay: boolean;

    constructor(position: Position, newPosition: Position, decay: boolean) {
        super();
        this.position = position;
        this.newPosition = newPosition;
        this.decay = decay;
    }
}
