import {observer} from "mobx-react";
import React, {Component} from "react";

import GameStore from "app/stores/GameStore";

import {Col, Row} from "react-bootstrap";

import Playground from "app/components/Game/Playground/Playground";
import GameMenu from "app/components/Game/GameMenu/GameMenu";

import "./GameScene.scss";

@observer
export default class GameScene extends Component {
    render() {
        return (
            <div className="game-scene">
                <Row>
                    <Col md={9}>
                        <Playground map={GameStore.map}/>
                    </Col>

                    <Col md={3}>
                        <GameMenu character={GameStore.character}/>
                    </Col>
                </Row>
            </div>
        )
    }
}
