// @flow

import {observer} from "mobx-react";
import React from "react";

import SceneStore from "app/stores/SceneStore";

import CharacterSelectionScene from "app/game/scenes/CharacterSelection/CharacterSelectionScene";
import SettingsScene from "app/game/scenes/Settings/SettingsScene";

import background from "app/assets/images/background.jpg";
import music from "app/assets/sounds/intro.mp3";

import "./MenuScene.scss";

@observer
export default class MenuScene extends React.PureComponent {
    componentWillMount() {
        SceneStore.setBackground(background);
        SceneStore.setMusic(music);
    }

    handleStart() {
        SceneStore.setScene(<CharacterSelectionScene/>);
    }

    handleSettings() {
        SceneStore.setModalScene(<SettingsScene/>);
    }

    render() {
        return (
            <div className="MenuScene">
                <div className="container">
                    <button onClick={this.handleStart.bind(this)}>
                        Start Game
                    </button>

                    <button onClick={this.handleSettings.bind(this)}>
                        Settings
                    </button>
                </div>
            </div>
        )
    }
}
