// @flow

import Spell from "app/models/Spell";
import Piece from "app/models/Piece";
import GameStore from "app/stores/GameStore";
import icon from "./bat-pocket-icon.png"

export default class BatPocketSpell extends Spell {
    static title = 'Bat Pocket';
    static castable = true;
    static cooldowns = [30];
    static costs = [30];

    static icon = icon;

    piece: Piece;

    cast() {
        this.piece = GameStore.piece;
        GameStore.newPiece(this.piece);
    }
}
