// @flow

import Spell from "app/game/Spell";
import Piece from "app/game/Piece";

import GameStore from "app/stores/GameStore";

export default class BatPocketSpell extends Spell {
    name = 'Bat Pocket';
    castable: boolean = true;

    castable = true;
    cooldown = 30;
    manaCost = 30;

    piece: Piece;

    cast() {
        this.piece = GameStore.piece;
        GameStore.newPiece(this.piece);
    }
}
