import React from "react";

import Spell from "app/game/Spell";
import PreviewEffect from "app/game/effects/PreviewEffect/PreviewEffect";

import GameStore from "app/stores/GameStore";

import Matrix from "app/game/Matrix";

import "./BatVisionSpell";

export default class BatVisionSpell extends Spell {
    name = 'Bat Vision';

    constructor() {
        super();
        GameStore.addEffect(new PreviewEffect());
    }

    renderWidget() {
        return (
            <div className="BatVisionSpell">
                <Matrix matrix={GameStore.piece}/>
            </div>
        );
    }
}
