// @flow

export default class TimePlayer {
    interval: number;
    callback: Function;
    timeoutId: number;

    constructor(interval: number, callback: Function) {
        this.interval = interval;
        this.callback = callback;
    }

    play() {
        this.timeoutId = setTimeout(() => this.play(), this.interval);
    }

    pause() {
        clearTimeout(this.timeoutId);
        this.timeoutId = 0;
    }

    accelerate(time: number): number {
        let newTime = this.interval - time;
        return this.interval = newTime;
    }
}
