// @flow

import Piece from "app/models/Piece";
import Character from "app/models/Character";

export default class Block {
    piece: Piece | null;
    color: any;
    character: Character;
    id: number;

    static _id = 1;

    constructor(piece: any, color: any, character: any = null) {
        this.piece = piece;
        this.color = color;
        this.character = character;
        this.id = this.constructor._id++;
    }

    detach() {
        this.piece = null;
    }
}
