// @flow

export default class Timer {
    interval: number;
    callback: Function;
    timeoutId: number;

    constructor(interval: number, callback: Function) {
        this.interval = interval;
        this.callback = callback;
    }

    play() {
        this.timeoutId = setTimeout(() => this.loop(), this.interval);
    }

    loop() {
        this.callback();
        this.play();
    }

    reset() {
        this.pause();
        this.play();
    }

    pause() {
        clearTimeout(this.timeoutId);
        this.timeoutId = 0;
    }

    stop() {
        this.pause();
    }
}
