// @flow

import {action, observable} from "mobx";
import React from "react";

import Store from "app/stores/Store";

class ConfigStore extends Store {
    @observable fxVolume: number = .25;
    @observable musicVolume: number = .1;
    @observable mute: boolean = false;

    @action setFxVolume(fxVolume: number): void {
        this.fxVolume = fxVolume;
    }

    @action setMusicVolume(musicVolume: number): void {
        this.musicVolume = musicVolume;
    }

    @action setMute(mute: boolean) {
        this.mute = mute;
    }
}

export default new ConfigStore();
