import {action, observable} from "mobx";
import Store from "app/stores/Store";

import React from "react";
import CharacterSelectionScene from "app/game/scenes/CharacterSelection/CharacterSelectionScene";

import background from "app/images/background.jpg";

class SceneStore extends Store {
    @observable.shallow scene = <CharacterSelectionScene/>;
    @observable background: string = background;

    @action change(scene, background) {
        this.scene = scene;
        this.background = background;
    }
}

export default new SceneStore();
