// @flow

import {action, observable} from "mobx";

import Store from "app/stores/Store";

class SceneStore extends Store {
    @observable scene: any;
    @observable modalScene: ?any;

    @observable background: string;
    @observable music: string;

    @action setScene(scene: any): void {
        this.scene = scene;
    }

    @action setModalScene(modalScene: ?any = undefined): void {
        this.modalScene = modalScene;
    }

    @action setBackground(background: string): void {
        this.background = background;
    }

    @action setMusic(music: string): void {
        this.music = music;
    }
}

export default new SceneStore();
