// @flow

import {action, observable} from "mobx";
import React from "react";

import Store from "app/stores/Store";

import MenuScene from "app/game/scenes/Menu/MenuScene";

class SceneStore extends Store {
    @observable.shallow scene = <MenuScene/>;
    @observable background: string;
    @observable music: string;

    @action setScene(scene) {
        this.scene = scene;
    }

    @action setBackground(background: string) {
        this.background = background;
    }

    @action setMusic(music: string) {
        this.music = music;
    }
}

export default new SceneStore();
